INSERT INTO returned_items (item_name, description, category, return_date, location, campus, owner_name, owner_contact, finder_name, finder_contact, story) VALUES
('MacBook Pro 16-inch M3', 'Space Gray laptop with university stickers. Found in library study room and returned same day.', 'Electronics', '2025-12-15', 'Main Library - Study Room 3A', 'North Campus', 'Tyler Johnson', 'tjohnson@campus.edu', 'Grace Martinez', 'gmartinez@campus.edu', 'Found the laptop left on a desk after closing time. Checked the stickers and recognized the owner from class. Contacted them immediately and met up to return it. They were so relieved!'),
('Vera Bradley Wallet', 'Floral pattern wallet with all cards and cash intact. Returned within hours of being lost.', 'Wallet', '2025-12-14', 'Campus Bookstore - Checkout Counter', 'Central Campus', 'Hannah Wilson', 'hwilson@campus.edu', 'Marcus Thompson', 'mthompson@campus.edu', 'Saw someone drop their wallet while leaving the bookstore. Ran after them but they were too far. Turned it into the bookstore staff who contacted the owner. Happy ending!'),
('Patagonia Backpack', 'Black backpack with laptop and textbooks. Owner was frantically searching for it.', 'Bag', '2025-12-13', 'Student Union - Cafeteria', 'East Campus', 'Zoe Anderson', 'zanderson@campus.edu', 'Lucas Garcia', 'lgarcia@campus.edu', 'Found the backpack under a table. Checked inside for ID and found a student card. Posted on social media and the owner responded within 30 minutes. Met up and returned everything safely.'),
('Rolex Submariner Watch', 'Expensive watch found in gym locker room. Owner was incredibly grateful.', 'Jewelry', '2025-12-12', 'Recreation Center - Locker Room', 'South Campus', 'Ethan Davis', 'edavis@campus.edu', 'Ava Rodriguez', 'arodriguez@campus.edu', 'Found this watch on the bench in the locker room. Knew it was valuable so immediately turned it in to the front desk. Owner came looking for it the next day. They were so happy they offered a reward, but I declined. Just happy to help!'),
('Prescription Glasses', 'Black frame glasses in hard case. Returned to very grateful owner who could not see without them.', 'Personal Items', '2025-12-11', 'Library - Computer Lab', 'West Campus', 'Chloe Martinez', 'cmartinez@campus.edu', 'Noah Thompson', 'nthompson@campus.edu', 'Found these glasses on a computer desk. Posted in our class group chat and the owner responded immediately. They had been looking everywhere! Met up within 10 minutes to return them. They were so relieved they could see again for their exam the next day.');